import React, { useEffect, useRef } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../assets/css/animate.min.css';
import '../assets/css/aos.min.css';
import '../assets/fonts/font-awesome.min.css';
import '../assets/css/style-font.css';
import Collapse from 'bootstrap/js/dist/collapse';

function Nav(paramDict) {
  const mainNavRef = useRef(null);

  useEffect(() => {
    const mainNav = mainNavRef.current;
    if (mainNav) {
      const navbarCollapse = mainNav.querySelector('.navbar-collapse');

      if (navbarCollapse) {
        const collapse = new Collapse(navbarCollapse, { toggle: false });
        const navbarItems = navbarCollapse.querySelectorAll('a');

        const handleClick = () => collapse.hide();

        navbarItems.forEach(item => {
          item.addEventListener('click', handleClick);
        });

        return () => {
          navbarItems.forEach(item => {
            item.removeEventListener('click', handleClick);
          });
        };
      }
    }
  }, []);

  useEffect(() => {
    const collapseNavbar = () => {
      const scrollTop = (window.pageYOffset !== undefined) ? window.pageYOffset : (document.documentElement || document.body.parentNode || document.body).scrollTop;

      if (scrollTop > 100) {
        mainNavRef.current.classList.add("navbar-shrink");
      } else {
        mainNavRef.current.classList.remove("navbar-shrink");
      }
    };

    collapseNavbar();
    document.addEventListener("scroll", collapseNavbar);

    return () => {
      document.removeEventListener("scroll", collapseNavbar);
    };
  }, []);

  return (
    <section id="page-top" data-bs-spy="scroll" data-bs-target="#mainNav" data-bs-offset="77">
      <nav className="navbar navbar-light navbar-expand-md fixed-top" id="mainNav" ref={mainNavRef}>
        {/* Your existing navbar content goes here */}
      </nav>
    </section>
  )
}

export default Nav;
