import React, {useRef, useState} from "react";
import FullCalendar from "@fullcalendar/react"
import dayGridPlugin from "@fullcalendar/daygrid"
import AddEventModal from "./AddEventModal";
import moment from "moment";

function Calendar() {
    const [modalOpen, setModalOpen] = useState(false);
    const [events, setEvents] = useState([])
    const calendarRef = useRef(null);

    const onEventAdded = event => {
        let calendarApi = calendarRef.current.getApi();
        calendarApi.addEvent({
            start: moment(event.start).toDate(),
            end: moment(event.end).toDate(),
            contenu: event.contenu
        });
    };

    async function handleEventAdd(data) {
        try {
            const response = await fetch("http://localhost:3000/api/calendar/create-event", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(data.event),
            });
    
            if (!response.ok) {
                console.error("Error adding event:", response.statusText);
            }
        } catch (error) {
            console.error("Error in handleEventAdd:", error);
        }
    }
    
    async function handleDatesSet(data) {
        try {
            const start = moment(data.start).toISOString();
            const end = moment(data.end).toISOString();
            const response = await fetch(`http://localhost:3000/api/calendar/get-events?demarre=${start}&termine=${end}`, { 
                timeout: 5000 
                });
    
            if (response.ok) {
                const events = await response.json();
                setEvents(events);
            } else {
                console.error("Error getting events:", response.statusText);
            }
        } catch (error) {
            console.error("Error in handleDatesSet:", error);
        }
    }

    return (
        <section>
            <button onClick={() => setModalOpen(true)}>Add Event</button>
                <div style={{position: "relative", zIndex: 0}}>
                    <FullCalendar
                        ref={calendarRef}
                        events={events}
                        plugins={[ dayGridPlugin ]}
                        initialView="dayGridMonth"
                        eventAdd={event => handleEventAdd(event)}
                        datesSet={(date) => handleDatesSet(date)}
                    />
                </div>

            <AddEventModal isOpen={modalOpen}
                           onClose={() => setModalOpen(false)}
                           onEventAdded={event => onEventAdded(event)}
            />

        </section>
    )
}

export default Calendar;
