function Temoignages(paramDict) {//doit commencer avec une majuscule
    function eventHandler(){
        console.log("Template")
    }

    async function postTemoignage() {
        await fetch(`http://localhost:3000/rendezvous?` + new URLSearchParams({
            rdvDate: '2023-04-13',
        }), { //port 3000 = serveur backend
            method: 'GET',
        })
            .then(response => console.log(response) || response.json()) //1e ".then" => le fetch a renvoyé une "response", response.json renvoie une promesse au 2 eme ".then"
            .then(data => { //2e ".then" = "data" contiend le payload de la réponse (un objet créé a partir d'un message json)
                ///////TRAITEMENT DE L'OBJET OBTENU
                console.log(data.valeur[0].rdvDate);
                return false
                return(data.valeur[0].rdvDate)

                //////FIN
            })
            .catch(error => console.error(error));

    }

    return(
        <div>
            <form onSubmit={postTemoignage}>
                <label>
                    Name:
                    <input type="text" name="name" />
                </label>
                <button type="submit" value="Submit" />
            </form>
            <button onClick={eventHandler}>{paramDict.param}bouton</button>
        </div>
    )
}

export default Temoignages;
